insert into categories (id, name) VALUES (nextval('public.category_id_seq'::regclass), 'Абрикоси');
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Світлоградський','Абрикос Світлоградський - це ранній, літній сорт, дозріває в другій декаді червня. Розмір плодів 50-60 грам. Плоди помаранчевого забарвлення без рум’янцю, м’якоть оранжева. Використовується для вживання в свіжому вигляді, транспортабельність гарна. Поодинокі плоди розтріскуються, але не більше 3-5% і ще в зеленому вигляді.',6.1);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'NJA-19','Абрикос NJA-19 - це літній середньостиглий сорт (збирання врожаю в середині липня), великоплідний із медовим смаком. Плоди великі, масою від 100 до 180 грам, кулястої форми, жовто-жовтогарячого кольору з червонуватим рум’янцем на сонячній стороні. М’якуш світло-жовтий, середньощільний, м’ясистий і соковитий, з медовим присмаком і ароматом. Кісточка невеликих розмірів, солодка, добре відокремляться від м’якоті. Абрикос використовують як у свіжому вигляді, так і в приготуванні компотів, для заморозки, варення та інших заготовок.',5.9);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Ледана','Абрикос Ледана – середній сорт абрикосу чеської селекції. Плоди дозрівають на початку липня, округло-овальної форми, оранжеві, без вираженого рум’янцю, маса 50-70 грам, м’якуш щільний, високі смакові якості, ароматні. Плоди стійкі до несприятливих погодних умов.',5.95);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Цегледі Бібер','Абрикос Цегледі Бібер – це старий сорт, який вже перевірений часом. Плоди світло-помаранчеві, присутні розмитий червоний рум’янець, маса 75-85 грам, окремі екземпляри можуть важити 100 грам. Смак фрукта дуже збалансований. Колір м’якоті – помаранчевий. Плоди великі і смачні. Їх можна використовувати для вживання як в свіжому вигляді, так і для переробки.',6.29);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Джумбо Тардив','Абрикос Джумбо Тардів – новий сорт абрикосу канадської селекції, середнього терміну дозрівання, збирання врожаю з другої декади липня. Плоди великі, трохи довгастої форми, середньою масою 120 - 150 грам, найбільші до 200 грам. Кісточка відокремлюється добре. Плоди яскраво-жовтогарячого забарвлення з невеликою засмагою, з щільною дуже приємною на смак ароматною м’якоттю, з гарною транспортабельністю.',5.49);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Кіото','Абрикос Кіото - новий сорт абрикоса французької селекції середнього терміну дозрівання (у середині липня). Середня вага плодів - 65 грам, окремі плоди більші. Плоди округло-подовженої форми, оранжеві, з яскраво-червоним рум’янцем на 30-70% поверхні плода. Смак дуже добрий, солодкий, ароматний. М’якуш помаранчевий.',6);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Харгранд','Абрикос Харгранд - канадський сорт абрикоса, пізнього терміну дозрівання. Знімання плодів у другій половині липня. Плоди дуже великі, середньою вагою 70 г, окремі плоди часто досягають маси до 120 г, подовжено-овальної форми. Колір плодів оранжевий, з невеликим червоним рум’янцем. Зовні дуже привабливі і мають гарний попит на ринку. Плоди високих смакових якостей, з медовим присмаком, дуже солодкі, м’якуш дуже соковитий, ароматний, універсального призначення, транспортабельний. Кісточка відокремлюється у недозрілому стані і не відокремлюється при повному дозріванні.',5.9);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Апріум','Абрикос Апріум - колоновидний абрикос схрещений із сливою (склад 75% абрикоса + 25% сливи). Вам буде чим здивувати Ваших гостей, такого Ви ще не коштували. Зовнішній вигляд плодів більше схожий на абрикоси, він помаранчевий, але без характерного опушування і волокнистості. Шкірка гладка з блиском як у сливи. Дозрівання з перших чисел серпня, середня маса плодів 60 - 70 грам, м’якуш дуже приємного смаку, щільний, дуже солодкий з апельсиновим післясмаком і незвичайним приємним ароматом. Кісточка відділяється добре. Плоди мають властивість дозрівання, після зриву можуть деякий час зберігатися і дозрівати.',5.95);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Банзай','Абрикос Банзай виведений у Франції, селекція характеризується супер ранніми термінами дозрівання плодів. Збирання врожаю в умовах півдня України з другої половини червня. Плоди великі, середньою масою 50 - 65 грам, овальні, насичено помаранчевого забарвлення з розмитим червоним рум’янцем. М’якуш щільний, волокнистий, солодкий, з приємним ароматом.',6.29);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Єгипетський Фараон','Абрикос Єгипетський Фараон відрізняється надраннім терміном дозрівання, на початку червня. Плоди великі 80 - 100 грам, м’якуш соковитий, без прожилок. Забарвлення плодів жовте-жовтогаряче. Плоди мають незрівнянний аромат і високі смакові якості.',5.49);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Персиковий','Абрикос Персиковий – середньостиглий сорт, першими плодами можна поласувати наприкінці липня – на початку серпня. Плоди великі, масою 40 - 60 грам, світло-жовтого кольору з рожевим рум’янцем. Вони вкриті легким пушком, як персики, звідси, мабуть, назва сорту. Форма плодів витягнута, горбиста, явно виражений черевний шов посередині. М’якуш абрикосів жовтий, солодкий, досить щільний, без кислинки. Встигають плоди нерівномірно, можна збирати кілька разів на літо. Є слабкий персиковий аромат. Кісточка відокремлюється погано.',6);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Пріція','Абрикос Пріція – новий дуже ранній сорт абрикосу селекції Франції. Дозрівання врожаю на початку червня. Належить до надранніх сортів абрикоса. Плоди середніх та великих розмірів масою 60 - 80 грам, що для дуже раннього сорту є відмінним показником. Плоди округлої форми, помаранчевого забарвлення, з рівномірним малиновим рум’янцем на більшій поверхні плода, який часто заповнює 100% поверхні при гарному освітленні сонцем. Плоди зовні дуже красиві і привабливі. М’якуш плодів помаранчевий, дуже соковитий, щільний, з відмінним збалансованим кисло-солодким смаком.',5.9);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Ред Тардів','Абрикос Ред Тардів - середньо-ранній, великоплідний, невибагливий. Це новий цікавий сорт канадського походження. Плоди дуже великі, в середньому маса становить 120-150 грам (деякі плоди можуть досягати до 200 г), округлої форми, повністю покриті насиченим червоним рум’янцем. М’якуш оранжевого забарвлення, дуже ароматний, з кислинкою. Плоди абрикоса Ред Тардів споживаються у свіжому та переробленому вигляді (компоти, джеми, варення, сухофрукти).',5.95);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Рубіста','Абрикос Рубіста - новий ранній сорт абрикоса селекції Франції. Дозрівання врожаю – 15-20 червня. Сорт відноситься до нових великоплідних ранніх сортів з інтенсивним забарвленням плодів. Маса плодів 70 - 80 грам, окремі плоди більші. Форма плодів округла. Забарвлення плодів інтенсивне, рубіново-червоного кольору, дуже помітне. На сонячному боці плоди практично повністю покриті рум’янцем. М’якуш плодів дуже соковитий, середньої щільності, відмінного збалансованого кисло-солодкого смаку, з запашним абрикосовим ароматом.',6.29);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Том Кот','Абрикос Том Кот - американський сорт абрикоса середнього терміну дозрівання. Отриманий від схрещування сортів Rival та Goldrich. Плоди великі середньою вагою 80 грам, окремі екземпляри досягають 110 грам, видовжено-кулястої форми, вершина плода злегка звужена. Рум’янець червоного кольору на 10-40% поверхні. Дуже хороший збалансований смак, із специфічним присмаком. М’якуш помаранчевий, дуже щільний. Плоди привабливі та товарні.',5.49);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Фарбелі','Абрикос Фарбелі - новий пізній сорт абрикоса. Селекція Франції. Плоди дуже великі, округло-подовженої форми, масою 90 - 100 грам, окремі більші, досягають 140 грам, насичено-оранжевого кольору, з червоним рум’янцем на 30-60% поверхні плода. При повному дозріванні плоди майже повністю стають червоні. Смакові якості сорту високі.',6);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Фардао','Абрикос Фардао – новий дуже пізній сорт абрикосу з дозріванням плодів у вересні. Селекція Франції. Плоди масою близько 50 грам, окремі крупніше, видовжено-конічної форми, злегка загострені до вершини, вираженим швом, привабливого яскраво-жовтогарячого забарвлення з не сильно вираженим точковим червоним рум’янцем. М’якуш щільної консистенції, помаранчевий, дуже соковитий, хрящуватий і повністю м’який при дозріванні. Смакові якості, високі, смак практично повністю солодкий, із невеликим вмістом кислоти, дуже ароматні.',5.9);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Фаркло','Абрикос Фаркло – новий надпізній сорт абрикосу селекції Франції. Плоди великі, масою 50 - 70 грам, округло-овальної форми, звужені до вершини, чітким швом, іноді чітко виділяється ""носик"" вершини плода, яскраво-жовтого кольору, іноді з нечітким крапчастим рум’янцем з сонячного боку. В основному плоди монотонні у кольорі, на що позначаються пізні терміни дозрівання та нестача тепла в цей період. М’якуш щільний, середньої соковитості, злегка сухуватий, хрумкий, що властиво багатьом пізнім сортам. Смакові якості хороші, солодкі, із невеликим вмістом кислоти.',5.95);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Цунамі','Абрикос Цунамі – новий ранній сорт абрикосу французької селекції. Плоди дуже великі, масою 70 - 90 грам і більше, подовжено-циліндричної форми, оранжево-жовті, покриті на 60-70% червоним рум’янцем. М’якуш помаранчевий, соковитий, смак солодкий, дуже хороший, збалансований.',6.29);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Чорний Персиковий','Абрикос Чорний Персиковий - гібрид між абрикосом та аличем, взяв усе найкраще від батьків, відрізняється привабливими плодами та високими смаковими якостями. Це літній сорт, плоди дозрівають пізно у серпні. М’якуш має ніжний, абрикосовий аромат. Маса плодів дорівнює приблизно 40 грам, формою вони нагадують кулю, можуть бути округло-овальними. М’якуш має темно-бордовий колір, кісточка від м’якоті відокремлюється з легкістю. Плоди придатні для транспортування, зберігання, тому сорт застосовується як промисловий.',5.49);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Бержерон','Абрикос Бержерон – сорт абрикоса середньо-пізнього терміну дозрівання, французька селекція. Дозрівання плодів Бержерона – 20-ті числа липня. Маса плодів - 50 - 60 грам і більше, округлої форми, з яскраво-червоним точковим, яскравим рум’янцем на більшій частині плода. Зовні плоди дуже гарні, товарні. Плоди мають яскравий аромат. М’якуш дуже щільний, оранжевого кольору, солодкий, з приємною кислинкою. Смак дуже добрий, ароматний, високих смакових якостей.',6);
insert into products (id, category_id, name, info, price) VALUES (nextval('public.product_id_seq'::regclass), 1,'Біг Ред','Абрикос Біг Ред - унікальний червоний абрикос французької селекції, середнього терміну дозрівання (друга декада липня). Плоди виглядають дуже привабливо та апетитно, яскраво-червоний рум’янець, що покриває половину абрикоса. Маса плоду досягає 100 грам, сорт транспортабельний. М’якуш ніжний, соковитий, солодкий, добре відділяється від кісточки.',5.79);
